import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const CreatePost = () => {
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    image: '',
    tags: '',
    status: 'published'
  });
  const [loading, setLoading] = useState(false);
  const [imageFile, setImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState('');
  const [uploadingImage, setUploadingImage] = useState(false);
  const navigate = useNavigate();

  const { title, content, image, tags, status } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      
      // Create preview
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const uploadImage = async () => {
    if (!imageFile) return '';

    setUploadingImage(true);
    const formData = new FormData();
    formData.append('image', imageFile);

    try {
      const res = await axios.post('/api/posts/upload-image', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setUploadingImage(false);
      return res.data.imageUrl;
    } catch (error) {
      setUploadingImage(false);
      toast.error('Error uploading image');
      return '';
    }
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    
    if (!title.trim() || !content.trim()) {
      toast.error('Title and content are required');
      return;
    }

    setLoading(true);

    try {
      // Upload image if selected
      let imageUrl = image.trim();
      if (imageFile) {
        imageUrl = await uploadImage();
      }

      const postData = {
        title: title.trim(),
        content: content.trim(),
        image: imageUrl,
        tags: tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        status
      };

      const res = await axios.post('/api/posts', postData);
      toast.success('Post created successfully!');
      navigate(`/post/${res.data.post._id}`);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Error creating post');
    }
    
    setLoading(false);
  };

  return (
    <div className="container">
      <form onSubmit={onSubmit} className="post-form">
        <h2 className="form-title">
          <i className="fas fa-plus"></i> Create New Post
        </h2>
        
        <div className="form-group">
          <label htmlFor="title">Title *</label>
          <input
            type="text"
            id="title"
            name="title"
            value={title}
            onChange={onChange}
            className="form-control"
            placeholder="Enter post title"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="content">Content *</label>
          <textarea
            id="content"
            name="content"
            value={content}
            onChange={onChange}
            className="form-control"
            placeholder="Write your post content here..."
            rows="10"
            required
          ></textarea>
        </div>

        <div className="form-group">
          <label htmlFor="image">Image URL (optional)</label>
          <input
            type="url"
            id="image"
            name="image"
            value={image}
            onChange={onChange}
            className="form-control"
            placeholder="https://example.com/image.jpg"
          />
        </div>

        <div className="form-group">
          <label htmlFor="tags">Tags (optional)</label>
          <input
            type="text"
            id="tags"
            name="tags"
            value={tags}
            onChange={onChange}
            className="form-control"
            placeholder="javascript, react, nodejs (comma separated)"
          />
          <small className="text-muted">
            Separate tags with commas
          </small>
        </div>

        <div className="form-group">
          <label htmlFor="status">Status</label>
          <select
            id="status"
            name="status"
            value={status}
            onChange={onChange}
            className="form-control"
          >
            <option value="published">Published</option>
            <option value="draft">Draft</option>
          </select>
        </div>

        <div className="form-group">
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={loading}
            style={{ marginRight: '10px' }}
          >
            {loading ? (
              <>
                <i className="fas fa-spinner fa-spin"></i> Creating...
              </>
            ) : (
              <>
                <i className="fas fa-plus"></i> Create Post
              </>
            )}
          </button>
          
          <button 
            type="button" 
            onClick={() => navigate('/')}
            className="btn btn-secondary"
          >
            <i className="fas fa-times"></i> Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreatePost;
